import React, {useState} from 'react';
import Kiva from '../../images/kiva.svg';
import UNDP from '../../images/undp.svg';
import MakeAWish from '../../images/makeawish.svg';
import GlobalTassels from '../../images/globaltassels.svg';

const PhilantrophySection = (dark, properties) => {
    const [property, setProperty] = useState();
    return (
        <div className={"section" + (dark ? " section-dark" : "")}>
            <div className="section-content" id='section3'>
                <h1 className='sectionTitle'>Philantrophy</h1>
                <p className="philantrophySection">
                    Sapphire Global Associates, LLC is committed to the worldview of philanthropic giving and
                development. Percentage of its assets are gifted to a selected list of charities with missions to
                transformation our world by effectively responding to assist in economic development,
                livelihoods and longevity, social change, and improving issues we have brought upon ourselves
                in our world.
                </p>

                <div className="cards-slider">
                    <div className="cards-slider-wrapper">
                        {
                            properties.map(property => {<Card property={property}/>})
                        }
                        <Card property={property}/>
                    </div>
                </div>
                <a href='https://www.kiva.org/'> <img src={Kiva} className='partnerLogo logo' alt="Kiva Logo"/> </a>
                <p className='missionStatement'>  We envision a financially inclusive world where all people hold the power to improve their lives. <br/> </p>
                <a href='https://www.undp.org/content/undp/en/home.html'> <img src={UNDP} className='undpLogo logo' alt="UNDP Logo"/> </a>
                <p className='missionStatement'> UNDP works in about 170 countries and territories, helping to achieve the eradication of poverty, and the reduction of inequalities and exclusion. We help countries to develop policies, leadership skills, partnering abilities, institutional capabilities and build resilience in order to sustain development results. <br/> </p>
                <a href='https://wish.org/'> <img src={MakeAWish} className='partnerLogo logo' alt="Make-A-Wish Logo"/> </a>
                <p className="missionStatement"> Together, we create life-changing wishes for children with critical illnesses. <br/> </p>
                <a href='http://globaltassels.org/'> <img src={GlobalTassels} className='globalTasselsLogo logo' alt="Global Tassels Logo"/> </a>
                <p className='missionStatement'> We open doors to colleges and universities worldwide and we are committed to empowering our students to be leaders in their fields; engaging them in their communities; supporting their living sustainability, and shaping them to become the voices and forces of change for their countries. <br/> </p>
            </div>
        </div>
    )
};

const Card = ({ property }) => {
    const {index, pic, name, missionStatement} = property;
    return (
        <div>
            <img src={pic} alt={name + " logo"}/>
            <div className="details">
                <span className="index">{index+1}</span>
                <p className="missionStatement">
                    {missionStatement}
                </p>
            </div>

        </div>
    )
}

export default PhilantrophySection;
